shader.wgsl:
fn getColor(lineCoord: vec2f) -> vec4f {
  // Start cap (negative x): red
  // End cap (positive x): blue
  // Line body (x == 0): gray
  if (lineCoord.x < -0.01) {
    return vec4f(0.9, 0.2, 0.2, 1.0);  // Red for start cap
  } else if (lineCoord.x > 0.01) {
    return vec4f(0.2, 0.2, 0.9, 1.0);  // Blue for end cap
  } else {
    return vec4f(0.5, 0.5, 0.5, 1.0);  // Gray for body
  }
}
